function addScript(src) {
  var node = document.createElement('script');
  node.type = 'text/javascript';
  node.async = true;
  node.src = src;
  (document.getElementsByTagName('head')[0] ||
      document.getElementsByTagName('body')[0]).appendChild(node);
}

